            array('id' => '21','key' => 'overview-right-section','value' => '{"language":{"en":{"title":"Why Dr. Kaifala Marah?"},"es":{"title":null},"ar":{"title":null},"fr":{"title":null},"hi":{"title":null}},"items":{"678f8de88532b":{"language":{"en":{"heading":"Economic Development","details":"Dr. Kaifala Marah is a proven economist with a strong vision for revitalizing Sierra Leone\\u2019s economy. His expertise in financial management and governance positions him as a leader who can drive sustainable economic growth, job creation, and investment opportunities. With a focus on reducing inflation, strengthening local industries, and ensuring financial transparency, Dr. Marah is committed to building a resilient economy that benefits all Sierra Leoneans. His leadership will prioritize economic policies that empower small businesses, attract foreign investment, and create employment opportunities, particularly for the youth."},"es":{"heading":null,"details":null},"ar":{"heading":null,"details":null},"fr":{"heading":null,"details":null},"hi":{"heading":null,"details":null}},"id":"678f8de88532b","image":"fc0527c9-cfe7-4e0f-b1b8-55952afacbca.webp"},"678f90c278057":{"language":{"en":{"heading":"Healthcare Reform","details":"A healthy nation is a prosperous nation, and Dr. Kaifala Marah understands the urgent need for healthcare reform in Sierra Leone. He advocates for improved access to quality healthcare, better medical infrastructure, and increased investment in healthcare professionals. His plan includes expanding community health services, enhancing maternal and child healthcare, and ensuring affordable medication for all citizens. By prioritizing healthcare policies that strengthen public health institutions, he aims to reduce preventable diseases and improve the overall well-being of the people."},"es":{"heading":null,"details":null},"ar":{"heading":null,"details":null},"fr":{"heading":null,"details":null},"hi":{"heading":null,"details":null}},"id":"678f90c278057","image":"c7b6bbf1-d5e0-44aa-9d3e-a9e5a67d29df.webp"},"678f91037d1c7":{"language":{"en":{"heading":"Education and Youth Empowerment","details":"Kaifala Marah believes that education is the foundation of national development. His vision includes expanding access to quality education, modernizing school curricula, and investing in teacher training. He is committed to ensuring that every child in Sierra Leone, regardless of background, has the opportunity to receive a quality education that prepares them for the future. Additionally, he plans to create more vocational training programs and higher education opportunities to equip young people with the skills needed to compete in the global economy. With a people-first approach, Dr. Kaifala Marah is ready to lead Sierra Leone into a future of economic prosperity, better healthcare, and a strong education system that empowers the next generation."},"es":{"heading":null,"details":null},"ar":{"heading":null,"details":null},"fr":{"heading":null,"details":null},"hi":{"heading":null,"details":null}},"id":"678f91037d1c7","image":"c3a505d2-4b3b-4f39-8ae3-2a92a18ac590.webp"}}}','status' => '1','serialize' => NULL,'created_at' => '2025-01-21 17:56:01','updated_at' => '2025-01-21 19:39:31')
